param(
    $key,
    $data
)
$data | ConvertTo-SecureString -key $key `
| ForEach-Object {[Runtime.InteropServices.Marshal]::PtrToStringAuto([Runtime.InteropServices.Marshal]::SecureStringToBSTR($_))}










